import Vue from 'vue'
import VueRouter from 'vue-router'
import Layout from '@/views/Layout.vue'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    component: Layout,
    redirect: '/dashboard',
    children: [
      {
        path: 'dashboard',
        name: 'Dashboard',
        component: () => import('@/views/Dashboard.vue'),
        meta: { title: '仪表盘' }
      },
      {
        path: 'project',
        name: 'ProjectList',
        component: () => import('@/views/Project/ProjectList.vue'),
        meta: { title: '项目管理' }
      },
      {
        path: 'project/detail/:id',
        name: 'ProjectDetail',
        component: () => import('@/views/Project/ProjectDetail.vue'),
        meta: { title: '项目详情' }
      },
      {
        path: 'deliverable',
        name: 'DeliverableList',
        component: () => import('@/views/Deliverable/DeliverableList.vue'),
        meta: { title: '交付物管理' }
      },
      {
        path: 'deliverable/detail/:id',
        name: 'DeliverableDetail',
        component: () => import('@/views/Deliverable/DeliverableDetail.vue'),
        meta: { title: '交付物详情' }
      },
      {
        path: 'deliverable/add',
        name: 'DeliverableAdd',
        component: () => import('@/views/Deliverable/DeliverableForm.vue'),
        meta: { title: '新增交付物' }
      },
      {
        path: 'deliverable/edit/:id',
        name: 'DeliverableEdit',
        component: () => import('@/views/Deliverable/DeliverableForm.vue'),
        meta: { title: '编辑交付物' }
      }
    ]
  }
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

// 路由守卫
router.beforeEach((to, from, next) => {
  // 设置页面标题
  if (to.meta.title) {
    document.title = `${to.meta.title} - 项目交付物管理系统`
  }
  next()
})

export default router
