import request from '@/utils/request'

export const systemApi = {
  // 获取系统列表
  getSystems() {
    return request({
      url: '/systems',
      method: 'get'
    })
  },

  // 获取单个系统
  getSystem(id) {
    return request({
      url: `/systems/${id}`,
      method: 'get'
    })
  },

  // 创建系统
  createSystem(data) {
    return request({
      url: '/systems',
      method: 'post',
      data
    })
  },

  // 更新系统
  updateSystem(id, data) {
    return request({
      url: `/systems/${id}`,
      method: 'put',
      data
    })
  },

  // 删除系统
  deleteSystem(id) {
    return request({
      url: `/systems/${id}`,
      method: 'delete'
    })
  }
}
